import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Clock from './Clock.js';

const user = {
  firstName: 'Luca',
  lastName: 'Cesarano',
  avatarUrl: 'http://cdn.shopify.com/s/files/1/1158/9490/products/C000017107-PAR-ZOOM_f52ec3d1-0100-4919-8e77-1630a80511e4_800x.jpg?v=1525441030'
};

function formatName(user) {
  return user.firstName + ' ' + user.lastName;
}

function getGreeting(user) {
  if(user) {
    return <h1>Hello, {formatName(user)}!</h1>;
  } else return <h1>Hello, Stranger!</h1>;
}

function WelcomeTest() {
  return (
    <div>
      <Welcome name="Sara" />
      <Welcome name="Cahal" />
      <Welcome name="Edite" />
    </div>
  )
}

function createClock() {
  return new Clock;
}

function Welcome(props) {
  return <h1>Hello, {props.name}</h1>;
}

class App extends Component {
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            <Welcome name="Luca" ></Welcome>
            {createClock()}
          </p>
          <a
            className="App-link"
            href="https://reactjs.org/docs/state-and-lifecycle.html"
            target="_blank"
            rel="noopener noreferrer"
          >
            L'ultima volta sei arrivato fin qui, riprendi da dove hai interrotto!
          </a>
        </header>
      </div>
    );
  }
}

export default App;
